<tools>
{"tools": [{"toolspec": {"name": "get_stock_price", "description": "get the current stock price", "inputschema": {"json": {"type": "object", "properties": {"symbol": {"type": "string", "description": "the symbol of the stock, e.g. aapl"}}, "required": ["symbol"]}}}}]}
